function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".menu"),c=document.querySelector("body");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked")):(t.classList.add("active"),e.classList.add("active-burger"),c.classList.add("locked"))}));document.querySelectorAll(".menu__item").forEach((s=>{s.addEventListener("click",(function(){t.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(t.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav),document.addEventListener("DOMContentLoaded",(function(){if(!document.querySelector(".contacts__form"))return null;const e=document.querySelectorAll(".contacts__form-checkbox"),t=document.querySelectorAll(".contacts__form-input--checkbox");e.forEach((e=>{e.addEventListener("change",(()=>{t.forEach((e=>e.classList.remove("active")));const c=e.id+"-input";document.getElementById(c).classList.add("active")}))}))}));